SELECT T0.[Code] AS CODE_PERG      ,T0.[Name] AS NOME_PERG	  ,T1.U_Atividade	  ,T4.Name AS NOME_ATIVIDADE	  ,T2.Code AS CODE_RESP	  ,T3.NAME AS NOME_RESP	  ,case T4.Type  		when 5 then 'Rel'		when 9 then 'Rel_Int'		when 4 then 'Ped' end as Dep 	         FROM [dbo].[@LGOCPERG] T0 INNER JOIN  [dbo].[@LGOLCPERG] T1 ON T0.Code = T1.CODE INNER JOIN	 [dbo].[@LGOLCRESP] T2 ON T1.CODE = T2.U_Perg INNER JOIN  [dbo].[@LGOCRESP] T3 ON T2.Code = T3.Code INNER JOIN OCLS T4 ON T1.U_Atividade = T4.CODE ORDER BY T0.[Code],T0.[Name],T1.U_Atividade,T4.Name,T2.Code,T3.NAME