SELECT 	T0.OpprID AS NUM_OPORTUNIDADE,	(T8.firstNAME + T8.lastNAME) AS RESP_COMERCIAL,	T0.CardCode,	T0.CardName,	T2.CardType,	T1.Step_Id AS NUM_ETAPA,	T3.Descript AS ETAPA_PROSPECCAO,	T4.Descript AS Interesse,	CASE T0.Status 		WHEN 'w' THEN 'Venceu'		WHEN 'o' THEN 'Aberto' 		WHEN 'l' THEN 'Perdeu'	END AS STATUS_OPORTUNIDADE,	T7.Descript AS 'MOTIVO_PERDA',	T5.Descript AS FONTE_DE_INFORMACAO,	T1.ClosePrcnt AS PORCENTAGEM_PROSPECCAO,	T0.MaxSumLoc AS VALOR_POTENCIAL,	T2.U_LGO_Tot_Geral AS TOTAL_ALUNOS_ESCOLA_CAD_PN,	T0.U_LGO_Tot_Geral AS TOTAL_ALUNO_OPORTUNIDADE	FROM OOPR T0	INNER JOIN OPR1 T1 ON T0.OpprID = T1.OpprID	INNER JOIN OCRD T2 ON T0.CardCode = T2.CardCode	INNER JOIN OOST T3 ON T1.Step_Id = T3.StepId	left JOIN OOIR T4 ON T0.IntRate = T4.Num	left JOIN OOSR T5 ON T0.Source = T5.Num	LEFT JOIN OPR5 T6 ON T6.OpportId = T0.OpprId	LEFT JOIN OOFR T7 ON T6.ReasondId = T7.Num	LEFT JOIN OHEM T8 ON T2.U_LGO_CodRespCom = T8.empID 	WHERE T2.CardType ='L' and 	T1.Line = (select max(Line) from OPR1 T1 where T0.OpprId = T1.OpprId)	GROUP BY T0.OpprID,T0.SlpCode,(T8.firstNAME + T8.lastNAME),T0.CardCode,T0.CardName,T2.CardType,T1.Step_Id,T3.Descript,		 T4.Descript,T0.Status,T7.Descript,T5.Descript,T1.ClosePrcnt,T0.MaxSumLoc,T2.U_LGO_Tot_Geral,T0.U_LGO_Tot_GeralORDER BY T0.CardCode