----RespostasAtividades_vw----USE [SBOPRODEI]GO/****** Object:  View [dbo].[LGO_RespostasAtividades_vw]    Script Date: 04/08/2015 09:22:32 ******/SET ANSI_NULLS ONGOSET QUOTED_IDENTIFIER ONGOCREATE VIEW [dbo].[LGO_RespostasAtividades_vw] asSELECT 'Código atividade' = T0.ClgCode,--'OBS atividade'    = T0.Details,'Cód. Tipo'		   = T0.CntctType,'Tipo'			   = T4.Name,'Cód. Assunto'	   = T0.CntctSbjct,'Assunto'		   = T5.Name,'Código PN'		   = T0.CardCode,'Nome do PN'       = (Select CardName from OCRD AX where T0.CardCode = AX.CardCode),'Colaborador'      = T1.U_Name,'Departamento'     = t6.Name,'Data inicio'      = convert (nvarchar,T0.Recontact,103),'Data de término'  =  convert (nvarchar,T0.EndDate,103),'Código da Pergunta' = T2.U_CodPerg,'Pergunta'			= T2.U_Pergunta,'Obs da Resposta'   = T2.U_OBS2,'Código da Resposta' = T3.U_CodResp,'Resposta'           = T3.U_Resposta,'SIM / NÃO'			= T3.U_ChecadoFROM OCLG T0JOIN OUSR T1 ON T0.AttendUser = T1.USERIDJOIN OUDP T6 ON T1.Department = T6.CodeJOIN [@LGCCLASAT] T2 ON T2.U_CodAtiv = T0.ClgCodeJOIN [@LGLCCLASAT] T3 ON T2.Code = T3.CodeJOIN OCLT T4 on T0.CntctType = T4.CodeJOIN OCLS T5 ON T0.CntctSbjct = T5.CodeWHERE T3.U_Checado = 'SIM' and T0.U_LGO_PARC_IMPLE >= '2014/2015'GO---ATIVIDADES ABERTAS/FECHADAS---SELECT 'N° atividade' = T0.ClgCode,'Cód. Departamento'		   = T0.CntctType,'Departamento'			   = T3.Name,'Cód. Atividade'	   = T0.CntctSbjct,'Atividade'		   = T4.Name,'Código PN'		   = T0.CardCode,'Nome do PN'       = (Select CardName from OCRD AX where T0.CardCode = AX.CardCode),CASE T0.Closed 	WHEN 'Y' THEN 'FECHADO'	WHEN 'N' THEN 'ABERTO'END STATUS,'Parceiria/Implementação' = T0.U_LGO_PARC_IMPLE,'Atribuido por' = T5.U_Name,'Atribuido a'      = T1.U_Name,'Data inicio'      = convert (nvarchar,T0.Recontact,103),'Data de término'  =  convert (nvarchar,T0.EndDate,103)FROM OCLG T0JOIN OUSR T1 ON T0.AttendUser = T1.USERIDJOIN OUDP T2 ON T1.Department = T2.CodeJOIN OCLT T3 on T0.CntctType = T3.CodeJOIN OCLS T4 ON T0.CntctSbjct = T4.CodeJOIN OUSR T5 ON T0.AssignedBy = T5.USERIDWHERE  T0.U_LGO_PARC_IMPLE >= '2014/2015'